const e=document.getElementById("emailInput"),t=document.getElementById("submitButton"),o=document.getElementById("myForm");let d=document.getElementById("formError");t.addEventListener("click",t=>{e.validity.valid?o.classList.remove("js-error"):(o.classList.add("js-error"),""==e.value?d.innerText="Whoops! It looks like you forgot to add your email":d.innerText="Please provide a valid email address")});